## This is a picoweb example showing a web page route# specification using view decorators (Flask style).#import picowebfrom erika import Erikaapp = picoweb.WebApp(__name__)erika = Erika()@app.route("/")def index(req, resp):    yield from picoweb.start_response(resp)    yield from resp.awrite("I can show you a table of <a href='print'> hello world</a>.")@app.route("/print")def squares(req, resp):    yield from picoweb.start_response(resp)    erika.print_string('Hello welt...')    yield from resp.awrite('printing... <a href=\'print\'>MORE!</a>')import ulogging as logginglogging.basicConfig(level=logging.INFO)